dev_server.js.content:
// Simple development server script
console.log('Starting dev server...');

// Import required modules
import { exec } from 'child_process';
import { createServer } from 'http';

// Start the dev server
const devProcess = exec('npm run dev', (error) => {
  if (error) {
    console.error('Error starting dev server:', error);
    process.exit(1);
  }
});

// Pipe output
devProcess.stdout.pipe(process.stdout);
devProcess.stderr.pipe(process.stderr);

// Create a simple health check server
const healthServer = createServer((req, res) => {
  res.writeHead(200, { 'Content-Type': 'application/json' });
  res.end(JSON.stringify({ status: 'ok', message: 'Development server is running' }));
});

// Start health check server
healthServer.listen(8080, '0.0.0.0', () => {
  console.log('Health check server started on port 8080');
});

// Handle process termination
process.on('SIGINT', () => {
  console.log('Shutting down servers...');
  healthServer.close();
  devProcess.kill();
  process.exit(0);
});

