import { spawn } from 'child_process';
import { dirname, join } from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

console.log('Starting InvestLease static server...');

// Spawn the server process
const serverProcess = spawn('node', ['static_server.js'], {
  stdio: 'inherit',
  cwd: __dirname
});

// Handle process events
serverProcess.on('error', (error) => {
  console.error(`Server process error: ${error.message}`);
});

serverProcess.on('exit', (code, signal) => {
  if (code !== 0) {
    console.error(`Server process exited with code ${code}, signal: ${signal}`);
  } else {
    console.log('Server process exited successfully');
  }
});

// Keep the parent process alive
process.on('SIGINT', () => {
  console.log('Parent process received SIGINT, forwarding to child process');
  serverProcess.kill('SIGINT');
});

// Log that the parent process is running
console.log('Parent process is running, server should be available at http://0.0.0.0:5000');

