run_app.js.content:
import { exec } from 'child_process';
import fs from 'fs';

console.log('Starting application...');

// Kill any previous running processes
try {
  if (fs.existsSync('server.pid')) {
    const pid = fs.readFileSync('server.pid', 'utf8').trim();
    console.log(`Attempting to kill previous process with PID: ${pid}`);
    try {
      process.kill(parseInt(pid), 'SIGTERM');
      console.log(`Killed process with PID: ${pid}`);
    } catch (err) {
      console.log(`Could not kill process: ${err.message}`);
    }
    fs.unlinkSync('server.pid');
  }
} catch (err) {
  console.error('Error handling previous process:', err);
}

// Start the server
console.log('Starting Express server with tsx...');
const server = exec('npx tsx server/index.ts');

// Save the PID
fs.writeFileSync('server.pid', server.pid.toString());
console.log(`Server started with PID: ${server.pid}`);

// Handle server output
server.stdout.on('data', (data) => {
  console.log(`SERVER: ${data.trim()}`);
});

server.stderr.on('data', (data) => {
  console.error(`SERVER ERROR: ${data.trim()}`);
});

// Handle server exit
server.on('exit', (code, signal) => {
  console.log(`Server process exited with code ${code} and signal ${signal}`);
});

// Keep the process running
process.on('SIGINT', () => {
  console.log('Shutting down...');
  try {
    process.kill(server.pid, 'SIGTERM');
  } catch (err) {
    console.error('Error shutting down server:', err);
  }
  process.exit(0);
});

console.log('Application startup complete!');

run_application.js.content:
/**
 * Run a server and a vite development server.
 */
const { spawn } = require('child_process');
const fs = require('fs');
const http = require('http');

// To clean up when the process exits
let serverProcess = null;
let healthcheckServer = null;

// Log with a timestamp
function log(message) {
  const now = new Date().toISOString();
  console.log(`[${now}] ${message}`);
}

// Clean up processes
function cleanup(code = 0) {
  log("Cleaning up processes...");
  if (serverProcess) {
    serverProcess.kill();
    serverProcess = null;
  }
  if (healthcheckServer) {
    healthcheckServer.close();
    healthcheckServer = null;
  }
  log("Cleanup complete");
  process.exit(code);
}

// Attach signal handlers for graceful shutdown
process.on('SIGINT', () => cleanup());
process.on('SIGTERM', () => cleanup());
process.on('exit', () => cleanup());

// Start the server
log("Starting InvestLease application server...");

// Start server using npm run dev
serverProcess = spawn('npm', ['run', 'dev'], {
  stdio: 'inherit',
  env: { ...process.env, PORT: '5000' }
});

// Wait for the server to start
serverProcess.on('error', (err) => {
  log(`Server error: ${err.message}`);
  cleanup(1);
});

serverProcess.on('close', (code) => {
  log(`Server process exited with code ${code}`);
  cleanup(code || 0);
});

// Create a simple healthcheck server
healthcheckServer = http.createServer((req, res) => {
  res.writeHead(200, { 'Content-Type': 'application/json' });
  res.end(JSON.stringify({ status: 'ok', server: 'running' }));
});

healthcheckServer.listen(8080, '0.0.0.0', () => {
  log('Healthcheck server running on port 8080');
});

