 * Run the InvestLease server using nodejs
 */

async function startServer() {
  const { spawn } = require('child_process');
  console.log('Starting InvestLease server...');

  try {
    const server = spawn('node', ['server.cjs'], {
      stdio: 'inherit',
      shell: true
