// service.js - Server runner for InvestLease
// This is a simple wrapper around npm run dev to ensure consistent server startup

import { spawnSync, spawn } from 'child_process';
import { createServer } from 'http';
import fs from 'fs';
import { fileURLToPath } from 'url';
import path from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Kill any previously running server
try {
  if (fs.existsSync('server.pid')) {
    const pid = parseInt(fs.readFileSync('server.pid', 'utf8').trim());
    if (pid) {
      console.log(`\x1b[33mAttempting to kill previous server process (PID: ${pid})\x1b[0m`);
      try {
        process.kill(pid);
      } catch (error) {
        // Ignoring errors if process doesn't exist
      }
    }
  }
} catch (err) {
  console.error('Error handling previous process:', err);
}

// Setup health check server
const healthServer = createServer((req, res) => {
  res.writeHead(200, { 'Content-Type': 'application/json' });
  res.end(JSON.stringify({ status: 'ok', timestamp: new Date().toISOString() }));
});

healthServer.listen(8080, '0.0.0.0', () => {
  console.log('\x1b[36mHealth check server running on port 8080\x1b[0m');
});

// Display banner
console.log('\x1b[33m=============================================\x1b[0m');
console.log('\x1b[33müè¢ InvestLease Application\x1b[0m');
console.log('\x1b[33m=============================================\x1b[0m');

// Start main application using npm run dev
console.log('\x1b[36mStarting application server...\x1b[0m');
const server = spawn('npm', ['run', 'dev'], {
  stdio: 'inherit',
  env: { ...process.env, PORT: '5000' }
});

// Save PID for future reference
fs.writeFileSync('server.pid', server.pid.toString());
console.log(`\x1b[32mServer started with PID: ${server.pid}\x1b[0m`);

// Handle server exit
server.on('exit', (code) => {
  console.log(`\x1b[31mServer process exited with code ${code}\x1b[0m`);
  cleanupAndExit(code || 0);
});

// Cleanup function
function cleanupAndExit(code = 0) {
  console.log('\x1b[33mCleaning up before exit...\x1b[0m');
  healthServer.close();
  process.exit(code);
}

// Handle process termination signals
process.on('SIGINT', () => {
  console.log('\n\x1b[33mReceived SIGINT signal, shutting down...\x1b[0m');
  server.kill('SIGINT');
  cleanupAndExit(0);
});

process.on('SIGTERM', () => {
  console.log('\n\x1b[33mReceived SIGTERM signal, shutting down...\x1b[0m');
  server.kill('SIGTERM');
  cleanupAndExit(0);
});

// Keep the process alive
process.stdin.resume();

