css/style.css.content:
/* General styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 15px;
}

a {
    text-decoration: none;
    color: #333;
}

ul {
    list-style: none;
}

/* Header styles */
.header {
    padding: 20px 0;
    background-color: #f8f8f8;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.header__inner {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 24px;
    font-weight: bold;
}

.menu__list {
    display: flex;
}

.menu__item {
    margin-left: 30px;
}

.menu__link {
    transition: color 0.3s;
}

.menu__link:hover,
.menu__link.active {
    color: #4285f4;
}

/* Main styles */
.main {
    padding: 50px 0;
}

/* Contacts section */
.contacts__title {
    margin-bottom: 30px;
    text-align: center;
}

.contacts__content {
    display: flex;
    flex-wrap: wrap;
    gap: 30px;
}

.contacts__info {
    flex: 1;
    min-width: 300px;
}

.contacts__item {
    margin-bottom: 25px;
}

.contacts__item-title {
    margin-bottom: 10px;
    font-size: 18px;
    font-weight: bold;
}

.contacts__item-text {
    margin-bottom: 5px;
}

/* Map container - FIXED STYLING */
.contacts__map-container {
    flex: 2;
    min-width: 300px;
    /* Set specific dimensions and ensure correct containment */
    height: 450px;
    position: relative;
    overflow: hidden; /* Prevent map from overflowing */
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.contacts__map {
    /* Make the map fill the entire container properly */
    position: absolute;
    top: 0;
    left: 0;
    width: 100% !important; /* !important to override any inline styles from Google Maps */
    height: 100% !important;
    border: none;
}

/* Footer styles */
.footer {
    padding: 40px 0;
    background-color: #f8f8f8;
    border-top: 1px solid #e7e7e7;
}

.footer__inner {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    gap: 30px;
}

.footer__info {
    flex: 1;
    min-width: 250px;
}

.footer__logo {
    display: inline-block;
    margin-bottom: 15px;
}

.footer__nav {
    flex: 1;
    min-width: 250px;
}

.footer__menu-item {
    margin-bottom: 10px;
}

.footer__menu-link {
    transition: color 0.3s;
}

.footer__menu-link:hover {
    color: #4285f4;
}

/* Responsive styles */
@media (max-width: 768px) {
    .contacts__content {
        flex-direction: column;
    }

    .contacts__map-container {
        /* Adjust height for smaller screens but maintain proportions */
        height: 350px;
        width: 100%;
    }
}

@media (max-width: 576px) {
    .header__inner {
        flex-direction: column;
        text-align: center;
    }

    .menu__list {
        margin-top: 20px;
        justify-content: center;
    }

    .menu__item {
        margin: 0 10px;
    }

    .contacts__map-container {
        /* Further reduce height for mobile */
        height: 300px;
    }
}

