// Initialize the map
function initMap() {
    // Default coordinates (can be adjusted to actual location)
    const coordinates = { lat: 55.7558, lng: 37.6173 }; // Moscow coordinates

    // Create map options with proper zoom level and controls
    const mapOptions = {
        zoom: 14,
        center: coordinates,
        mapTypeControl: true,
        streetViewControl: false,
        fullscreenControl: false,
        zoomControl: true,
        scrollwheel: false, // Prevents accidental zooming when scrolling the page
    };

    // Create the map
    const map = new google.maps.Map(document.getElementById('map'), mapOptions);

    // Add a marker at the specified location
    const marker = new google.maps.Marker({
        position: coordinates,
        map: map,
        title: '–ù–∞—à –æ—Ñ–∏—Å'
    });

    // Make sure the map properly fills its container on window resize
    google.maps.event.addDomListener(window, 'resize', function() {
        const center = map.getCenter();
        google.maps.event.trigger(map, 'resize');
        map.setCenter(center);
    });

    // Fix for the map sometimes not displaying properly on page load
    setTimeout(function() {
        google.maps.event.trigger(map, 'resize');
        map.setCenter(coordinates);
    }, 500);
}

// Handle map initialization errors
function handleMapError() {
    const mapContainer = document.getElementById('map');
    if (mapContainer) {
        mapContainer.innerHTML = '<div style="text-align: center; padding: 20px;">–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∫–∞—Ä—Ç—É. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç—É –∏–ª–∏ –æ–±–Ω–æ–≤–∏—Ç–µ —Å—Ç—Ä–∞–Ω–∏—Ü—É.</div>';
    }
}

// Execute when the window loads
window.onload = function() {
    // Check if Google Maps API loaded successfully
    if (typeof google === 'undefined') {
        handleMapError();
    }

    // Ensure map container is properly sized
    const mapContainer = document.querySelector('.contacts__map-container');
    if (mapContainer) {
        // Make sure container has explicit dimensions
        if (!mapContainer.style.height) {
            mapContainer.style.height = '450px';
        }
    }
};

