import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Slider } from "@/components/ui/slider";

export function Calculator() {
  const [carPrice, setCarPrice] = useState(1000000);
  const [initialPayment, setInitialPayment] = useState(20);
  const [leaseTerm, setLeaseTerm] = useState(36);

  // –†–∞—Å—á–µ—Ç –µ–∂–µ–º–µ—Å—è—á–Ω–æ–≥–æ –ø–ª–∞—Ç–µ–∂–∞
  const calculateMonthlyPayment = () => {
    const initialAmount = (carPrice * initialPayment) / 100;
    const leasedAmount = carPrice - initialAmount;
    const interestRate = 0.09 / 12; // 9% –≥–æ–¥–æ–≤—ã—Ö, –ø–µ—Ä–µ–≤–µ–¥–µ–Ω–Ω—ã–µ –≤ –º–µ—Å—è—á–Ω—É—é —Å—Ç–∞–≤–∫—É

    // –§–æ—Ä–º—É–ª–∞ —Ä–∞—Å—á–µ—Ç–∞ –µ–∂–µ–º–µ—Å—è—á–Ω–æ–≥–æ –ø–ª–∞—Ç–µ–∂–∞
    const payment = (leasedAmount * interestRate * Math.pow(1 + interestRate, leaseTerm)) / 
                   (Math.pow(1 + interestRate, leaseTerm) - 1);

    return Math.round(payment);
  };

  // –ü–æ–ª—É—á–∞–µ–º —Ä–∞—Å—á–µ—Ç–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è
  const monthlyPayment = calculateMonthlyPayment();
  const initialAmount = Math.round((carPrice * initialPayment) / 100);
  const totalAmount = initialAmount + (monthlyPayment * leaseTerm);

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
  const handleCarPriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseFloat(e.target.value.replace(/[^\d]/g, ""));
    setCarPrice(isNaN(value) ? 0 : value);
  };

  const handleInitialPaymentChange = (value: number[]) => {
    setInitialPayment(value[0]);
  };

  const handleLeaseTermChange = (value: number[]) => {
    setLeaseTerm(value[0]);
  };

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —á–∏—Å–µ–ª –¥–ª—è —É–¥–æ–±–Ω–æ–≥–æ –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
  const formatNumber = (num: number) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  };

  return (
    <div className="w-full glass backdrop-blur-md p-6 rounded-xl relative overflow-hidden border border-primary/20">
      <h3 className="text-xl font-semibold mb-6 relative">
        –ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –ª–∏–∑–∏–Ω–≥–∞
        <div className="absolute -bottom-2 left-1/2 transform -translate-x-1/2 w-20 h-[1px] bg-gradient-to-r from-transparent via-primary/70 to-transparent"></div>
      </h3>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
        {/* –õ–µ–≤–∞—è –∫–æ–ª–æ–Ω–∫–∞ - –≤–≤–æ–¥ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ */}
        <div className="space-y-6">
          <div className="space-y-3">
            <div className="flex justify-between">
              <Label htmlFor="car-price">–°—Ç–æ–∏–º–æ—Å—Ç—å –∏–º—É—â–µ—Å—Ç–≤–∞, ‚ÇΩ</Label>
              <span className="text-primary font-semibold">
                {formatNumber(carPrice)} ‚ÇΩ
              </span>
            </div>
            <Slider
              value={[carPrice]}
              min={500000}
              max={30000000}
              step={100000}
              onValueChange={(value) => setCarPrice(value[0])}
              className="my-4"
            />
            <div className="flex justify-between text-sm text-muted-foreground">
              <span>500 000 ‚ÇΩ</span>
              <span>30 000 000 ‚ÇΩ</span>
            </div>
            <Input
              id="car-price"
              value={formatNumber(carPrice)}
              onChange={handleCarPriceChange}
              className="bg-background/30 border-primary/30 mt-2"
            />
          </div>

          <div className="space-y-3">
            <div className="flex justify-between">
              <Label>–ü–µ—Ä–≤–æ–Ω–∞—á–∞–ª—å–Ω—ã–π –≤–∑–Ω–æ—Å, {initialPayment}%</Label>
              <span className="text-primary font-semibold">
                {formatNumber(initialAmount)} ‚ÇΩ
              </span>
            </div>
            <Slider
              value={[initialPayment]}
              min={10}
              max={60}
              step={1}
              onValueChange={handleInitialPaymentChange}
              className="my-4"
            />
            <div className="flex justify-between text-sm text-muted-foreground">
              <span>10%</span>
              <span>60%</span>
            </div>
          </div>

          <div className="space-y-3">
            <div className="flex justify-between">
              <Label>–°—Ä–æ–∫ –ª–∏–∑–∏–Ω–≥–∞, {leaseTerm} –º–µ—Å.</Label>
            </div>
            <Slider
              value={[leaseTerm]}
              min={12}
              max={60}
              step={12}
              onValueChange={handleLeaseTermChange}
              className="my-4"
            />
            <div className="flex justify-between text-sm text-muted-foreground">
              <span>12 –º–µ—Å.</span>
              <span>60 –º–µ—Å.</span>
            </div>
          </div>
        </div>

        {/* –ü—Ä–∞–≤–∞—è –∫–æ–ª–æ–Ω–∫–∞ - —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã */}
        <div className="flex flex-col justify-between h-full">
          <div className="space-y-8">
            <div className="glass backdrop-blur-lg bg-background/20 p-5 rounded-xl border border-primary/20">
              <div className="text-lg text-muted-foreground mb-1">–ï–∂–µ–º–µ—Å—è—á–Ω—ã–π –ø–ª–∞—Ç–µ–∂</div>
              <div className="text-3xl font-bold text-primary">{formatNumber(monthlyPayment)} ‚ÇΩ</div>
            </div>

            <div>
              <div className="mb-4">
                <div className="text-sm text-muted-foreground mb-1">–ü–µ—Ä–≤–æ–Ω–∞—á–∞–ª—å–Ω—ã–π –≤–∑–Ω–æ—Å</div>
                <div className="text-lg font-semibold">{formatNumber(initialAmount)} ‚ÇΩ</div>
              </div>

              <div className="mb-4">
                <div className="text-sm text-muted-foreground mb-1">–°—É–º–º–∞ –¥–æ–≥–æ–≤–æ—Ä–∞ –ª–∏–∑–∏–Ω–≥–∞</div>
                <div className="text-lg font-semibold">{formatNumber(totalAmount)} ‚ÇΩ</div>
              </div>
            </div>
          </div>

          <Button className="w-full mt-6" size="lg">
            –û—Å—Ç–∞–≤–∏—Ç—å –∑–∞—è–≤–∫—É –Ω–∞ –ª–∏–∑–∏–Ω–≥
          </Button>
        </div>
      </div>

      {/* Decorative —ç–ª–µ–º–µ–Ω—Ç—ã */}
      <div className="absolute top-10 right-10 w-20 h-20 rounded-full bg-primary/5 blur-xl"></div>
      <div className="absolute bottom-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-primary/80 to-transparent"></div>
    </div>
  );
}

