import { useState, useEffect, useRef } from 'react';
import '@/styles/russia-map.css';

interface Region {
  id: string;
  name: string;
  code: string;
  city: string;
  address: string;
  phone: string;
  email: string;
}

interface SimpleRussiaMapProps {
  regions: Region[];
  onRegionSelect: (region: Region) => void;
}

export const SimpleRussiaMap = ({ regions, onRegionSelect }: SimpleRussiaMapProps) => {
  const [hoveredRegion, setHoveredRegion] = useState<string | null>(null);
  const [activeRegion, setActiveRegion] = useState<string | null>("krasnodar");
  const [isMapReady, setIsMapReady] = useState(false);
  const mapPointsRef = useRef<HTMLDivElement>(null);

  // Initialize map with animation
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsMapReady(true);
    }, 500);
    return () => clearTimeout(timer);
  }, []);

  // Add pulse effects to map points
  useEffect(() => {
    if (!isMapReady || !mapPointsRef.current) return;

    // Clear any existing pulse elements first
    const existingPulses = mapPointsRef.current.querySelectorAll('.map-point-pulse');
    existingPulses.forEach(pulse => pulse.remove());

    // Add pulse for each region with staggered delay for visual effect
    regions.forEach((region, index) => {
      const coordinates = getPointPosition(region.code);
      if (!coordinates.left || !coordinates.top) return;

      const pulse = document.createElement('div');
      pulse.className = 'map-point-pulse';
      pulse.style.left = coordinates.left as string; 
      pulse.style.top = coordinates.top as string;

      // Add staggered animation delay
      pulse.style.animationDelay = `${index * 0.2}s`;

      if (mapPointsRef.current) {
        mapPointsRef.current.appendChild(pulse);
      }
    });
  }, [regions, isMapReady]);

  const handleRegionHover = (code: string, isHovering: boolean) => {
    if (isHovering) {
      setHoveredRegion(code);
    } else {
      setHoveredRegion(null);
    }
  };

  const handleRegionClick = (code: string) => {
    const region = regions.find(r => r.code === code);
    if (region) {
      setActiveRegion(region.id);
      onRegionSelect(region);
    }
  };

  return (
    <div className={`simple-russia-map-container transition-opacity duration-1000 ${isMapReady ? 'opacity-100' : 'opacity-0'}`}>      
      <div className="map-container">
        {/* –ö–∞—Ä—Ç–∞ –†–æ—Å—Å–∏–∏ –≤ –≤–∏–¥–µ —Ç–æ—á–µ–∫ –≥–æ—Ä–æ–¥–æ–≤ */}
        <div className="map-outline">
          <div className="map-background">
            <div className="map-svg-container">
              <div className="russia-map-outline glow-effect"></div>
            </div>
            <div className="map-title">
              –ö–∞—Ä—Ç–∞ —Ñ–∏–ª–∏–∞–ª–æ–≤
              <div className="h-[1px] w-24 mx-auto mt-1 bg-gradient-to-r from-transparent via-primary/40 to-transparent"></div>
            </div>
          </div>

          {/* –¢–æ—á–∫–∏ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ä–µ–≥–∏–æ–Ω–∞ –Ω–∞ –∫–∞—Ä—Ç–µ */}
          <div className="map-points" ref={mapPointsRef}>
            {regions.map((region, index) => (
              <div
                key={region.id}
                className={`map-point ${hoveredRegion === region.code || activeRegion === region.id ? 'active' : ''}`}
                style={{
                  left: getPointPosition(region.code).left as string,
                  top: getPointPosition(region.code).top as string,
                  transitionDelay: isMapReady ? `${index * 0.05}s` : '0s'
                }}
                onMouseEnter={() => handleRegionHover(region.code, true)}
                onMouseLeave={() => handleRegionHover(region.code, false)}
                onClick={() => handleRegionClick(region.code)}
              >
                {hoveredRegion === region.code && (
                  <div className="city-label">
                    {region.city}
                  </div>
                )}
              </div>
            ))}
          </div>

          {/* Add subtle panning gradient effect */}
          <div className="absolute inset-0 pointer-events-none opacity-20 shimmer-effect"></div>
        </div>
      </div>
    </div>
  );
};

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è –ø–æ–∑–∏—Ü–∏–∏ —Ç–æ—á–∫–∏ –Ω–∞ –∫–∞—Ä—Ç–µ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ä–µ–≥–∏–æ–Ω–∞
const getPointPosition = (code: string): React.CSSProperties => {
  // –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ä–µ–≥–∏–æ–Ω–∞ (–≤ –ø—Ä–æ—Ü–µ–Ω—Ç–∞—Ö –æ—Ç —Ä–∞–∑–º–µ—Ä–∞ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞)
  const coordinates: Record<string, { left: string; top: string }> = {
    'RU-KDA': { left: '23%', top: '75%' },     // –ö—Ä–∞—Å–Ω–æ–¥–∞—Ä
    'RU-LEN': { left: '27%', top: '26%' },     // –°–∞–Ω–∫—Ç-–ü–µ—Ç–µ—Ä–±—É—Ä–≥
    'RU-NIZ': { left: '35%', top: '46%' },     // –ù–∏–∂–Ω–∏–π –ù–æ–≤–≥–æ—Ä–æ–¥
    'RU-TAM': { left: '32%', top: '53%' },     // –¢–∞–º–±–æ–≤
    'RU-SAM': { left: '43%', top: '56%' },     // –¢–æ–ª—å—è—Ç—Ç–∏
    'RU-TYU': { left: '58%', top: '43%' },     // –¢—é–º–µ–Ω—å
    'RU-ULY': { left: '39%', top: '54%' },     // –£–ª—å—è–Ω–æ–≤—Å–∫
    'RU-BA': { left: '48%', top: '52%' },      // –£—Ñ–∞
    'RU-CHE': { left: '52%', top: '48%' }      // –ß–µ–ª—è–±–∏–Ω—Å–∫
  };

  return coordinates[code] || { left: '50%', top: '50%' }; // –î–µ—Ñ–æ–ª—Ç–Ω–∞—è –ø–æ–∑–∏—Ü–∏—è
};

