import { Card, CardContent } from "@/components/ui/card";
import { StatCard } from "@/components/ui/stat-card";
import { RevenueChart } from "@/components/dashboard/revenue-chart";
import { OccupancyChart } from "@/components/dashboard/occupancy-chart";
import { PropertiesTable } from "@/components/dashboard/properties-table";
import { RecentActivity } from "@/components/dashboard/recent-activity";
import { useQuery } from "@tanstack/react-query";
import { DashboardStats, PropertyData, ActivityData, RevenueChartData, OccupancyChartData } from "@/types";
import { useState, useEffect } from "react";
import { useAutoSync } from "@/hooks/use-auto-sync";

export default function Dashboard() {
  // Auto-sync documents on first login
  useAutoSync();

  // Fetch dashboard statistics
  const { data: stats, isLoading: statsLoading } = useQuery<DashboardStats>({
    queryKey: ['/api/stats/dashboard'],
  });

  // Fetch properties
  const { data: properties, isLoading: propertiesLoading } = useQuery<PropertyData[]>({
    queryKey: ['/api/properties'],
  });

  // Fetch recent activities
  const { data: activities, isLoading: activitiesLoading } = useQuery<ActivityData[]>({
    queryKey: ['/api/activities/recent'],
  });

  // Fetch revenue chart data
  const { data: revenueData, isLoading: revenueLoading } = useQuery<RevenueChartData[]>({
    queryKey: ['/api/charts/revenue'],
  });

  // Fetch occupancy chart data
  const { data: occupancyData, isLoading: occupancyLoading } = useQuery<OccupancyChartData>({
    queryKey: ['/api/charts/occupancy'],
  });

  return (
    <div className="p-6">
      {/* Header */}
      <header className="mb-6">
        <h1 className="text-2xl font-bold text-slate-900 mb-2">Dashboard</h1>
        <p className="text-slate-600">Welcome back to your investment and lease management portal.</p>
      </header>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <StatCard
          title="Total Properties"
          value={statsLoading ? "Loading..." : `${stats?.totalProperties || 0}`}
          trend={statsLoading ? "..." : `${stats?.propertyTrend || "0"}`}
          trendDirection="up"
          icon={
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-5 h-5">
              <path strokeLinecap="round" strokeLinejoin="round" d="M12 21v-8.25M15.75 21v-8.25M8.25 21v-8.25M3 9l9-6 9 6m-1.5 12V10.332A48.36 48.36 0 0012 9.75c-2.551 0-5.056.2-7.5.582V21M3 21h18M12 6.75h.008v.008H12V6.75z" />
            </svg>
          }
          iconBgColor="bg-emerald-50"
          iconTextColor="text-emerald-600"
        />

        <StatCard
          title="Active Leases"
          value={statsLoading ? "Loading..." : `${stats?.activeLeases || 0}`}
          trend={statsLoading ? "..." : `${stats?.leaseTrend || "0"}`}
          trendDirection="up"
          icon={
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-5 h-5">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 12h3.75M9 15h3.75M9 18h3.75m3 .75H18a2.25 2.25 0 002.25-2.25V6.108c0-1.135-.845-2.098-1.976-2.192a48.424 48.424 0 00-1.123-.08m-5.801 0c-.065.21-.1.433-.1.664 0 .414.336.75.75.75h4.5a.75.75 0 00.75-.75 2.25 2.25 0 00-.1-.664m-5.8 0A2.251 2.251 0 0113.5 2.25H15c1.012 0 1.867.668 2.15 1.586m-5.8 0c-.376.023-.75.05-1.124.08C9.095 4.01 8.25 4.973 8.25 6.108V8.25m0 0H4.875c-.621 0-1.125.504-1.125 1.125v11.25c0 .621.504 1.125 1.125 1.125h9.75c.621 0 1.125-.504 1.125-1.125V9.375c0-.621-.504-1.125-1.125-1.125H8.25zM6.75 12h.008v.008H6.75V12zm0 3h.008v.008H6.75V15zm0 3h.008v.008H6.75V18z" />
            </svg>
          }
          iconBgColor="bg-blue-50"
          iconTextColor="text-blue-600"
        />

        <StatCard
          title="Monthly Revenue"
          value={statsLoading ? "Loading..." : `$${stats?.monthlyRevenue?.toLocaleString() || 0}`}
          trend={statsLoading ? "..." : `${stats?.revenueTrend || "0"}`}
          trendDirection="up"
          icon={
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-5 h-5">
              <path strokeLinecap="round" strokeLinejoin="round" d="M12 6v12m-3-2.818l.879.659c1.171.879 3.07.879 4.242 0 1.172-.879 1.172-2.303 0-3.182C13.536 12.219 12.768 12 12 12c-.725 0-1.45-.22-2.003-.659-1.106-.879-1.106-2.303 0-3.182s2.9-.879 4.006 0l.415.33M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
