import { DataTable } from "@/components/ui/data-table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { LeaseData } from "@/types";
import { useLocation } from "wouter";
import { format } from "date-fns";

export default function Leases() {
  const [, navigate] = useLocation();

  // Fetch leases
  const { data: leases, isLoading } = useQuery<LeaseData[]>({
    queryKey: ['/api/leases'],
  });

  const columns = [
    {
      header: "Property",
      accessorKey: (lease: LeaseData) => (
        <div className="flex items-center">
          <div className="flex-shrink-0 h-10 w-10 bg-slate-100 rounded flex items-center justify-center">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6 text-slate-500">
              <path strokeLinecap="round" strokeLinejoin="round" d="M12 21v-8.25M15.75 21v-8.25M8.25 21v-8.25M3 9l9-6 9 6m-1.5 12V10.332A48.36 48.36 0 0012 9.75c-2.551 0-5.056.2-7.5.582V21M3 21h18M12 6.75h.008v.008H12V6.75z" />
            </svg>
          </div>
          <div className="ml-4">
            <div className="text-sm font-medium text-slate-900">{lease.propertyName}</div>
            <div className="text-sm text-slate-500">Unit {lease.unitNumber}</div>
          </div>
        </div>
      ),
    },
    {
      header: "Tenant",
      accessorKey: "tenantName",
    },
    {
      header: "Monthly Rent",
      accessorKey: (lease: LeaseData) => {
        return `$${lease.monthlyRent.toLocaleString()}`;
      },
    },
    {
      header: "Period",
      accessorKey: (lease: LeaseData) => {
        try {
          const startDate = format(new Date(lease.startDate), 'MMM d, yyyy');
          const endDate = format(new Date(lease.endDate), 'MMM d, yyyy');
          return (
            <div className="text-sm text-slate-700">
              <div>{startDate}</div>
              <div className="text-slate-500">to</div>
              <div>{endDate}</div>
            </div>
          );
        } catch (e) {
          return 'Invalid date';
        }
      },
    },
    {
      header: "Status",
      accessorKey: (lease: LeaseData) => {
        const getStatusColor = (status: string) => {
          switch (status.toLowerCase()) {
            case 'active':
              return (
                <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
                  Active
                </Badge>
              );
            case 'expired':
              return (
                <Badge className="bg-slate-100 text-slate-800 hover:bg-slate-100">
                  Expired
                </Badge>
              );
            case 'terminated':
              return (
                <Badge className="bg-red-100 text-red-800 hover:bg-red-100">
                  Terminated
                </Badge>
              );
            default:
              return (
                <Badge className="bg-slate-100 text-slate-800 hover:bg-slate-100">
                  {status}
                </Badge>
              );
          }
        };

        return getStatusColor(lease.status);
      },
    },
    {
      header: "Actions",
      accessorKey: (lease: LeaseData) => (
        <div>
          <Button 
            variant="link" 
            className="text-primary hover:text-primary/90 mr-3 p-0"
            onClick={() => navigate(`/leases/${lease.id}`)}
          >
            View
          </Button>
          <Button 
            variant="link" 
            className="text-slate-600 hover:text-slate-900 p-0"
            onClick={() => navigate(`/leases/${lease.id}/edit`)}
          >
            Edit
          </Button>
        </div>
      ),
    },
  ];

  return (
    <div className="p-6">
      <header className="mb-6">
        <h1 className="text-2xl font-bold text-slate-900 mb-2">Leases</h1>
        <p className="text-slate-600">Manage your lease agreements</p>
      </header>

      <div className="bg-white rounded-lg shadow-sm border border-slate-200 p-5">
        <DataTable
          data={leases || []}
          columns={columns}
          title="All Leases"
          actions={{
            onAdd: () => navigate('/leases/add'),
            addButtonLabel: "Add Lease"
          }}
        />
      </div>
    </div>
  );
}

