import { DataTable } from "@/components/ui/data-table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { TenantData } from "@/types";
import { useLocation } from "wouter";

export default function Tenants() {
  const [, navigate] = useLocation();

  // Fetch tenants
  const { data: tenants, isLoading } = useQuery<TenantData[]>({
    queryKey: ['/api/tenants'],
  });

  const columns = [
    {
      header: "Name",
      accessorKey: (tenant: TenantData) => (
        <div className="flex items-center">
          <div className="flex-shrink-0 h-10 w-10 bg-slate-100 rounded-full flex items-center justify-center">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6 text-slate-500">
              <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 6a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0zM4.501 20.118a7.5 7.5 0 0114.998 0A17.933 17.933 0 0112 21.75c-2.676 0-5.216-.584-7.499-1.632z" />
            </svg>
          </div>
          <div className="ml-4">
            <div className="text-sm font-medium text-slate-900">{tenant.name}</div>
            <div className="text-sm text-slate-500">{tenant.email}</div>
          </div>
        </div>
      ),
    },
    {
      header: "Contact",
      accessorKey: "phone",
    },
    {
      header: "Type",
      accessorKey: (tenant: TenantData) => {
        return tenant.type === 'individual' ? 'Individual' : 'Company';
      },
    },
    {
      header: "Payment Status",
      accessorKey: (tenant: TenantData) => {
        const getStatusColor = (status: string) => {
          switch (status.toLowerCase()) {
            case 'current':
              return (
                <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
                  Current
                </Badge>
              );
            case 'late':
              return (
                <Badge className="bg-yellow-100 text-yellow-800 hover:bg-yellow-100">
                  Late
                </Badge>
              );
            case 'overdue':
              return (
                <Badge className="bg-red-100 text-red-800 hover:bg-red-100">
                  Overdue
                </Badge>
              );
            default:
              return (
                <Badge className="bg-slate-100 text-slate-800 hover:bg-slate-100">
                  {status}
                </Badge>
              );
          }
        };

        return getStatusColor(tenant.paymentStatus);
      },
    },
    {
      header: "Actions",
      accessorKey: (tenant: TenantData) => (
        <div>
          <Button 
            variant="link" 
            className="text-primary hover:text-primary/90 mr-3 p-0"
            onClick={() => navigate(`/tenants/${tenant.id}`)}
          >
            View
          </Button>
          <Button 
            variant="link" 
            className="text-slate-600 hover:text-slate-900 p-0"
            onClick={() => navigate(`/tenants/${tenant.id}/edit`)}
          >
            Edit
          </Button>
        </div>
      ),
    },
  ];

  return (
    <div className="p-6">
      <header className="mb-6">
        <h1 className="text-2xl font-bold text-slate-900 mb-2">Tenants</h1>
        <p className="text-slate-600">Manage your tenant information</p>
      </header>

      <div className="bg-white rounded-lg shadow-sm border border-slate-200 p-5">
        <DataTable
          data={tenants || []}
          columns={columns}
          title="All Tenants"
          actions={{
            onAdd: () => navigate('/tenants/add'),
            addButtonLabel: "Add Tenant"
          }}
        />
      </div>
    </div>
  );
}

client/src/styles/russia-map.css.content:
.russia-map-container {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
}

.russia-map {
  width: 100%;
  max-height: 500px;
  height: auto;
  margin: 0 auto;
  display: block;
}

.russia-map svg {
  overflow: hidden !important;
}

.map-container {
  max-width: 1182px;
  height: 606px;
  overflow: hidden !important;
  margin: 0 auto;
  position: relative;
  border: 1px solid rgba(180, 160, 70, 0.1);
  border-radius: 8px;
  box-shadow: 0 5px 20px rgba(180, 160, 70, 0.1);
}

.map-point {
  width: 8px;
  height: 8px;
  background-color: #b4a046;
  border-radius: 50%;
  position: absolute;
  box-shadow: 0 0 10px rgba(180, 160, 70, 0.8);
  transform: translate(-50%, -50%);
  z-index: 2;
  cursor: pointer;
  transition: all 0.3s ease;
}

.map-point:hover, .map-point.active {
  width: 12px;
  height: 12px;
  background-color: #c5b057;
  box-shadow: 0 0 15px rgba(200, 180, 90, 0.9);
}

.map-point.active:before {
  content: '';
  position: absolute;
  width: 24px;
  height: 24px;
  background-color: rgba(200, 180, 90, 0.3);
  border-radius: 50%;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: pulse-ring 1.5s infinite;
}

@keyframes pulse-ring {
  0% {
    transform: translate(-50%, -50%) scale(0.8);
    opacity: 1;
  }
  100% {
    transform: translate(-50%, -50%) scale(2);
    opacity: 0;
  }
}

.city-label {
  position: absolute;
  background-color: rgba(15, 15, 20, 0.8);
  color: white;
  padding: 6px 12px;
  border-radius: 4px;
  font-size: 14px;
  pointer-events: none;
  white-space: nowrap;
  transform: translateX(-50%);
  bottom: calc(100% + 15px);
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2));
  opacity: 0;
  animation: fadeIn 0.3s forwards;
  z-index: 5;
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateX(-50%) translateY(5px); }
  to { opacity: 1; transform: translateX(-50%) translateY(0); }
}

.city-label:after {
  content: '';
  position: absolute;
  top: 100%;
  left: 50%;
  margin-left: -8px;
  border-width: 8px;
  border-style: solid;
  border-color: rgba(15, 15, 20, 0.8) transparent transparent transparent;
  filter: drop-shadow(0 2px 2px rgba(0, 0, 0, 0.1));
}

/* Map point pulse animation */
.map-point-pulse {
  position: absolute;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  animation: pulse 3s infinite;
  background: radial-gradient(circle, rgba(185, 160, 70, 0.6) 0%, rgba(185, 160, 70, 0) 70%);
  box-shadow: 0 0 20px rgba(185, 160, 70, 0.3);
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
}

@keyframes pulse {
  0% {
    width: 20px;
    height: 20px;
    opacity: 0.8;
  }

  50% {
    width: 50px;
    height: 50px;
    opacity: 0.5;
  }

  100% {
    width: 20px;
    height: 20px;
    opacity: 0.8;
  }
}

/* Regions styling */
path.region {
  fill: #f5f5f5; /* Default light gray */
  stroke: #e0e0e0;
  stroke-width: 0.5;
  transition: all 0.3s ease;
}

path.region.has-office {
  fill: #e6dec8; /* Light gold/beige for regions with offices */
}

path.region:hover {
  fill: #ece4c5; /* Lighter gold on hover */
  filter: drop-shadow(0 0 5px rgba(180, 160, 70, 0.3));
}

path.region.active {
  fill: #d0c28c; /* Darker gold for active region */
  stroke: #b4a046;
  stroke-width: 1;
  filter: drop-shadow(0 0 8px rgba(180, 160, 70, 0.5));
}

/* Add styling for the Moscow region special highlight */
path.region[data-region-id="MOW"] {
  fill: #d4c692; /* Special highlight for Moscow */
}

path.region[data-region-id="MOW"]:hover {
  fill: #c5b057; /* Brighter highlight on hover */
}

