import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from "recharts";
import { OccupancyChartData } from "@/types";

interface OccupancyChartProps {
  data: OccupancyChartData;
  isLoading?: boolean;
}

export function OccupancyChart({ data, isLoading = false }: OccupancyChartProps) {
  const chartData = [
    { name: 'Occupied Units', value: data.occupied },
    { name: 'Vacant Units', value: data.vacant },
  ];

  const COLORS = ['hsl(168, 85%, 35%)', '#e2e8f0'];

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between pb-2">
        <CardTitle className="text-md font-medium">Occupancy Rate</CardTitle>
        <Button variant="ghost" size="sm">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-5 h-5">
            <path strokeLinecap="round" strokeLinejoin="round" d="M6.75 12a.75.75 0 11-1.5 0 .75.75 0 011.5 0zM12.75 12a.75.75 0 11-1.5 0 .75.75 0 011.5 0zM18.75 12a.75.75 0 11-1.5 0 .75.75 0 011.5 0z" />
          </svg>
        </Button>
      </CardHeader>
      <CardContent className="pt-0">
        {isLoading ? (
          <div className="h-64 w-full flex items-center justify-center">
            <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full"></div>
          </div>
        ) : (
          <div className="h-64 flex items-center justify-center">
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={chartData}
                  cx="50%"
                  cy="50%"
                  innerRadius={60}
                  outerRadius={90}
                  fill="#8884d8"
                  paddingAngle={1}
                  dataKey="value"
                  startAngle={90}
                  endAngle={-270}
                  label={({ cx, cy, midAngle, innerRadius, outerRadius, percent }) => {
                    if (percent === 0) return null;
                    if (percent < 0.1) return null;
                    const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
                    const x = cx + radius * Math.cos(-midAngle * (Math.PI / 180));
                    const y = cy + radius * Math.sin(-midAngle * (Math.PI / 180));
                    return (
                      <text 
                        x={x} 
                        y={y} 
                        fill="white" 
                        textAnchor="middle" 
                        dominantBaseline="central"
                        fontWeight="bold"
                      >
                        {`${(percent * 100).toFixed(1)}%`}
                      </text>
                    );
                  }}
                >
                  {chartData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip 
                  formatter={(value) => [`${value} units`, undefined]}
                  contentStyle={{ borderRadius: '0.5rem', border: '1px solid #e2e8f0', background: '#ffffff' }}
                />
              </PieChart>
            </ResponsiveContainer>
          </div>
        )}

        <div className="mt-4">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center">
              <span className="w-3 h-3 bg-primary rounded-full mr-2"></span>
              <span className="text-sm text-slate-700">Occupied Units</span>
            </div>
            <span className="text-sm font-medium">{data.occupied}</span>
          </div>
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <span className="w-3 h-3 bg-slate-200 rounded-full mr-2"></span>
              <span className="text-sm text-slate-700">Vacant Units</span>
            </div>
            <span className="text-sm font-medium">{data.vacant}</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

