import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useState } from "react";
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import { RevenueChartData } from "@/types";

interface RevenueChartProps {
  data: RevenueChartData[];
  isLoading?: boolean;
}

export function RevenueChart({ data, isLoading = false }: RevenueChartProps) {
  const [chartPeriod, setChartPeriod] = useState<'monthly' | 'quarterly' | 'yearly'>('monthly');

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between pb-2">
        <CardTitle className="text-md font-medium">Revenue Overview</CardTitle>
        <div className="flex space-x-2">
          <Button 
            variant={chartPeriod === 'monthly' ? 'secondary' : 'ghost'} 
            size="sm" 
            onClick={() => setChartPeriod('monthly')}
            className={chartPeriod === 'monthly' ? 'bg-primary-50 text-primary hover:bg-primary-100 hover:text-primary' : ''}
          >
            Monthly
          </Button>
          <Button 
            variant={chartPeriod === 'quarterly' ? 'secondary' : 'ghost'} 
            size="sm" 
            onClick={() => setChartPeriod('quarterly')}
            className={chartPeriod === 'quarterly' ? 'bg-primary-50 text-primary hover:bg-primary-100 hover:text-primary' : ''}
          >
            Quarterly
          </Button>
          <Button 
            variant={chartPeriod === 'yearly' ? 'secondary' : 'ghost'} 
            size="sm" 
            onClick={() => setChartPeriod('yearly')}
            className={chartPeriod === 'yearly' ? 'bg-primary-50 text-primary hover:bg-primary-100 hover:text-primary' : ''}
          >
            Yearly
          </Button>
        </div>
      </CardHeader>
      <CardContent className="pt-0">
        {isLoading ? (
          <div className="h-64 w-full flex items-center justify-center">
            <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full"></div>
          </div>
        ) : (
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <AreaChart
                data={data}
                margin={{
                  top: 10,
                  right: 10,
                  left: 0,
                  bottom: 0,
                }}
              >
                <defs>
                  <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="hsl(168, 85%, 35%)" stopOpacity={0.8}/>
                    <stop offset="95%" stopColor="hsl(168, 85%, 35%)" stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#e2e8f0" />
                <XAxis 
                  dataKey="month" 
                  axisLine={false} 
                  tickLine={false}
                  tick={{ fontSize: 12, fill: '#64748b' }}
                />
                <YAxis 
                  axisLine={false} 
                  tickLine={false}
                  tick={{ fontSize: 12, fill: '#64748b' }}
                  tickFormatter={(value) => `$${value.toLocaleString()}`}
                />
                <Tooltip 
                  formatter={(value: number) => [`$${value.toLocaleString()}`, 'Revenue']}
                  contentStyle={{ borderRadius: '0.5rem', border: '1px solid #e2e8f0', background: '#ffffff' }}
                />
                <Area 
                  type="monotone" 
                  dataKey="revenue" 
                  stroke="hsl(168, 85%, 35%)" 
                  fillOpacity={1} 
                  fill="url(#colorRevenue)"
                  strokeWidth={2} 
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

