    },
  ];

  useEffect(() => {
    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –∏ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ –∫–∞—Ä—Ç—ã
    const initializeMap = async () => {
      try {
        // –ó–∞–≥—Ä—É–∂–∞–µ–º GeoJSON –¥–∞–Ω–Ω—ã–µ –¥–ª—è –†–æ—Å—Å–∏–∏
        console.log('–ó–∞–≥—Ä—É–∂–∞—é GeoJSON –¥–∞–Ω–Ω—ã–µ...');
        const response = await fetch('/russia.geo.json');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const geoData: GeoJSON = await response.json();
        console.log('GeoJSON –¥–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω—ã');

        // –ï—Å–ª–∏ SVG —ç–ª–µ–º–µ–Ω—Ç —Å—É—â–µ—Å—Ç–≤—É–µ—Ç, —Ä–µ–Ω–¥–µ—Ä–∏–º –∫–∞—Ä—Ç—É
        if (svgRef.current) {
          console.log('–†–µ–Ω–¥–µ—Ä—é –∫–∞—Ä—Ç—É...');
          renderMap(geoData);
        }
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ GeoJSON –¥–∞–Ω–Ω—ã—Ö:', error);
      }
    };

    // –í—ã–∑—ã–≤–∞–µ–º –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—é
    initializeMap();
  }, []);

  // –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞—Ä—Ç—ã —Å D3.js
  const renderMap = (geoData: GeoJSON) => {
    if (!svgRef.current) return;

    console.log('–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–≥–∏–æ–Ω–æ–≤ –≤ GeoJSON:', geoData.features.length);

    // –û—á–∏—â–∞–µ–º SVG
    d3.select(svgRef.current).selectAll('*').remove();

    // –†–∞–∑–º–µ—Ä—ã SVG
    const width = 800;
    const height = 500;

    // –°–æ–∑–¥–∞–µ–º –ø—Ä–æ–µ–∫—Ü–∏—é –¥–ª—è –∫–∞—Ä—Ç—ã –†–æ—Å—Å–∏–∏
    const projection = d3.geoAlbers()
      .rotate([-105, 0])
      .center([0, 60])
      .parallels([50, 70])
      .scale(700)
      .translate([width / 2, height / 2]);

    // –°–æ–∑–¥–∞–µ–º path –≥–µ–Ω–µ—Ä–∞—Ç–æ—Ä
    const pathGenerator = d3.geoPath().projection(projection);

    // –°–æ–∑–¥–∞–µ–º SVG –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä
    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height)
      .attr('viewBox', `0 0 ${width} ${height}`)
      .attr('preserveAspectRatio', 'xMidYMid meet');

    // –î–æ–±–∞–≤–ª—è–µ–º –≥—Ä—É–ø–ø—É –¥–ª—è –≤—Å–µ–π –∫–∞—Ä—Ç—ã –∏ –ø—Ä–∏–º–µ–Ω—è–µ–º –º–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–∏–µ
    const g = svg.append('g');

    // –°–æ–∑–¥–∞–µ–º –∏ —Å—Ç–∏–ª–∏–∑—É–µ–º –ø—É—Ç–∏ –¥–ª—è —Ä–µ–≥–∏–æ–Ω–æ–≤
    g.selectAll('path')
      .data(geoData.features)
      .join('path')
      .attr('d', (d) => pathGenerator(d as any) || '')
      .attr('class', d => {
        const regionCode = d.properties.iso_3166_2;
        return `region ${regionsWithBranches.includes(regionCode) ? 'has-filial' : ''}`;
      })
      .attr('data-region', d => d.properties.iso_3166_2)
      .attr('data-name', d => d.properties.name)
      .on('mouseover', (event, d) => handleMouseOver(event, d))
      .on('mousemove', (event) => handleMouseMove(event))
      .on('mouseout', () => handleMouseOut())
      .on('click', (event, d) => handleRegionClick(event, d));

    // –î–æ–±–∞–≤–ª—è–µ–º –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å –∑—É–º–∞ –∏ –ø–µ—Ä–µ–º–µ—â–µ–Ω–∏—è
    const zoom = d3.zoom()
      .scaleExtent([1, 8])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });

    svg.call(zoom as any);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –¥–ª—è –∏–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏
  const handleMouseOver = (event: any, d: any) => {
    const regionCode = d.properties.iso_3166_2;
    const regionName = d.properties.name;

    if (tooltipRef.current) {
      // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–æ–¥—Å–∫–∞–∑–∫—É
      tooltipRef.current.style.visibility = 'visible';
      tooltipRef.current.innerHTML = `
        <div>${regionName}</div>
        ${regionsWithBranches.includes(regionCode) ? '<div class="has-branch">–ï—Å—Ç—å —Ñ–∏–ª–∏–∞–ª</div>' : ''}
      `;
    }

    // –î–æ–±–∞–≤–ª—è–µ–º –∫–ª–∞—Å—Å –ø—Ä–∏ –Ω–∞–≤–µ–¥–µ–Ω–∏–∏
    d3.select(event.currentTarget).classed('hover', true);
  };

  const handleMouseMove = (event: any) => {
    if (tooltipRef.current) {
      // –û–±–Ω–æ–≤–ª—è–µ–º –ø–æ–∑–∏—Ü–∏—é –ø–æ–¥—Å–∫–∞–∑–∫–∏
      tooltipRef.current.style.left = `${event.pageX + 15}px`;
      tooltipRef.current.style.top = `${event.pageY - 30}px`;
    }
  };

  const handleMouseOut = () => {
    if (tooltipRef.current) {
      // –°–∫—Ä—ã–≤–∞–µ–º –ø–æ–¥—Å–∫–∞–∑–∫—É
      tooltipRef.current.style.visibility = 'hidden';
    }

    // –£–¥–∞–ª—è–µ–º –∫–ª–∞—Å—Å –ø—Ä–∏ —É—Ö–æ–¥–µ –º—ã—à–∏
    d3.selectAll('.region').classed('hover', false);
  };

  const handleRegionClick = (event: any, d: any) => {
    const regionCode = d.properties.iso_3166_2;

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —Ñ–∏–ª–∏–∞–ª –≤ —Ä–µ–≥–∏–æ–Ω–µ
    if (regionsWithBranches.includes(regionCode)) {
      setSelectedRegion(regionCode);

      // –ù–∞—Ö–æ–¥–∏–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Ñ–∏–ª–∏–∞–ª–µ
      const branch = branchesData.find(b => b.regionCode === regionCode);
      if (branch) {
        setSelectedBranch(branch);
      }

      // –£–¥–∞–ª—è–µ–º –≤—ã–¥–µ–ª–µ–Ω–∏–µ —Å–æ –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤ –∏ –≤—ã–¥–µ–ª—è–µ–º –≤—ã–±—Ä–∞–Ω–Ω—ã–π
      d3.selectAll('.region').classed('selected', false);
      d3.select(event.currentTarget).classed('selected', true);
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è –≤—ã–±–æ—Ä–∞ —Ñ–∏–ª–∏–∞–ª–∞ –≤ –≤—ã–ø–∞–¥–∞—é—â–µ–º —Å–ø–∏—Å–∫–µ
  const handleBranchSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const branchId = parseInt(e.target.value);
    if (!branchId) {
      setSelectedBranch(null);
      setSelectedRegion(null);

      // –°–Ω–∏–º–∞–µ–º –≤—ã–¥–µ–ª–µ–Ω–∏–µ —Å–æ –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤
      d3.selectAll('.region').classed('selected', false);
      return;
    }

    const branch = branchesData.find(branch => branch.id === branchId);
    if (branch) {
      setSelectedBranch(branch);
      setSelectedRegion(branch.regionCode);

      // –£–¥–∞–ª—è–µ–º –≤—ã–¥–µ–ª–µ–Ω–∏–µ —Å–æ –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤ –∏ –≤—ã–¥–µ–ª—è–µ–º –≤—ã–±—Ä–∞–Ω–Ω—ã–π
      d3.selectAll('.region').classed('selected', false);
      d3.select(`path[data-region="${branch.regionCode}"]`).classed('selected', true);
    }
  };

  return (
    <div className="py-6">
      <div className="flex flex-col md:flex-row gap-8">
        <div className="w-full md:w-2/3">
          <h2 className="text-2xl font-bold mb-2 text-center">–ù–∞—à–∏ —Ñ–∏–ª–∏–∞–ª—ã –Ω–∞ –∫–∞—Ä—Ç–µ –†–æ—Å—Å–∏–∏</h2>
          <p className="text-sm text-muted-foreground mb-6 text-center">–í—ã–±–µ—Ä–∏—Ç–µ —Ä–µ–≥–∏–æ–Ω –¥–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ —Ñ–∏–ª–∏–∞–ª–µ</p>

          {/* D3 SVG –∫–∞—Ä—Ç–∞ */}
          <div className="relative mx-auto max-w-3xl">
            <svg ref={svgRef} className="w-full h-auto"></svg>
            <div ref={tooltipRef} className="map-tooltip" style={{ visibility: 'hidden' }}></div>
          </div>
        </div>

        <div className="w-full md:w-1/3">
          {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Ñ–∏–ª–∏–∞–ª–µ */}
