import { useState, useEffect, useRef } from 'react';
import './russia-map.css';

interface Region {
  id: string;
  name: string;
  code: string;
  city: string;
  address: string;
  phone: string;
  email: string;
}

interface SimpleRussiaMapProps {
  regions: Region[];
  onRegionSelect: (region: Region) => void;
}

export const SimpleRussiaMap = ({ regions, onRegionSelect }: SimpleRussiaMapProps) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const [hoveredRegion, setHoveredRegion] = useState<string | null>(null);
  const [mapLoaded, setMapLoaded] = useState(false);
  const mapContainerRef = useRef<HTMLDivElement>(null);

  // –ù–∞–π—Ç–∏ —Ä–µ–≥–∏–æ–Ω –ø–æ –∫–æ–¥—É
  const getRegionByCode = (code: string) => {
    return regions.find(r => r.code === code);
  };

  // –†–µ–≥–∏–æ–Ω—ã, –∫–æ—Ç–æ—Ä—ã–µ —É –Ω–∞—Å –µ—Å—Ç—å –≤ –¥–∞–Ω–Ω—ã—Ö
  const availableRegionCodes = regions.map(r => r.code);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∏ –æ–±—Ä–∞–±–æ—Ç–∫–∞ SVG –∫–∞—Ä—Ç—ã
  useEffect(() => {
    const loadSvg = async () => {
      try {
        const response = await fetch('/russia_map.svg');
        const svgText = await response.text();

        if (svgRef.current) {
          svgRef.current.innerHTML = svgText;

          // –î–æ–±–∞–≤–∏—Ç—å –∫–ª–∞—Å—Å—ã –∏ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –∫ —Ä–µ–≥–∏–æ–Ω–∞–º –Ω–∞ –∫–∞—Ä—Ç–µ
          const svgElement = svgRef.current;
          const regionElements = svgElement.querySelectorAll('path.region');

          regionElements.forEach(region => {
            const regionCode = region.getAttribute('id');

            if (regionCode && availableRegionCodes.includes(regionCode)) {
              region.classList.add('available');

              // –î–æ–±–∞–≤–ª—è–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –¥–ª—è –¥–æ—Å—Ç—É–ø–Ω—ã—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤
              region.addEventListener('mouseenter', () => {
                setHoveredRegion(regionCode);
              });

              region.addEventListener('mouseleave', () => {
                setHoveredRegion(null);
              });

              region.addEventListener('click', () => {
                const found = getRegionByCode(regionCode);
                if (found) {
                  onRegionSelect(found);
                }
              });
            }
          });

          // –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ SVG –ø–æ–º–µ—â–∞–µ—Ç—Å—è –≤–Ω—É—Ç—Ä–∏ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
          if (mapContainerRef.current && svgElement) {
            const containerRect = mapContainerRef.current.getBoundingClientRect();
            const svgWidth = containerRect.width;
            svgElement.style.maxWidth = `${svgWidth}px`;
            svgElement.style.maxHeight = "100%";
          }

          setMapLoaded(true);
        }
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ SVG –∫–∞—Ä—Ç—ã:', error);
      }
    };

    loadSvg();

    // –î–æ–±–∞–≤–∏–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
    const handleResize = () => {
      if (mapContainerRef.current && svgRef.current) {
        const containerRect = mapContainerRef.current.getBoundingClientRect();
        const svgWidth = containerRect.width;
        svgRef.current.style.maxWidth = `${svgWidth}px`;
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [regions, availableRegionCodes, onRegionSelect]);

  return (
    <div className="simple-russia-map-container" ref={mapContainerRef}>      
      <div className="map-container">
        {/* –ö–∞—Ä—Ç–∞ –†–æ—Å—Å–∏–∏ –≤ –≤–∏–¥–µ SVG */}
        <div className="map-outline">
          <div className="map-background">
            <svg 
              ref={svgRef} 
              className={`russia-map ${mapLoaded ? 'loaded' : ''}`} 
              viewBox="0 0 1200 600" 
              preserveAspectRatio="xMidYMid meet"
              style={{ maxWidth: "100%", height: "auto" }}
            />
            <div className="map-title">–ö–∞—Ä—Ç–∞ —Ñ–∏–ª–∏–∞–ª–æ–≤</div>
          </div>

          {/* –ú–µ—Ç–∫–∏ –≥–æ—Ä–æ–¥–æ–≤ */}
          <div className="map-points">
            {mapLoaded && hoveredRegion && (
              <div className="region-tooltip">
                {getRegionByCode(hoveredRegion)?.city || hoveredRegion}
              </div>
            )}

            {/* –¢–æ—á–∫–∏ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ä–µ–≥–∏–æ–Ω–∞ –Ω–∞ –∫–∞—Ä—Ç–µ */}
            {regions.map(region => (
              <div
                key={region.id}
                className={`map-point ${hoveredRegion === region.code ? 'active' : ''}`}
                style={getPointPosition(region.code)}
                onMouseEnter={() => setHoveredRegion(region.code)}
                onMouseLeave={() => setHoveredRegion(null)}
                onClick={() => onRegionSelect(region)}
              >
                {hoveredRegion === region.code && (
                  <div className="city-label">
                    {region.city}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è –ø–æ–∑–∏—Ü–∏–∏ —Ç–æ—á–∫–∏ –Ω–∞ –∫–∞—Ä—Ç–µ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ä–µ–≥–∏–æ–Ω–∞
const getPointPosition = (code: string): React.CSSProperties => {
  // –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ä–µ–≥–∏–æ–Ω–∞ (–≤ –ø—Ä–æ—Ü–µ–Ω—Ç–∞—Ö –æ—Ç —Ä–∞–∑–º–µ—Ä–∞ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞)
  const coordinates: Record<string, { left: string; top: string }> = {
    'RU-KDA': { left: '23%', top: '75%' },     // –ö—Ä–∞—Å–Ω–æ–¥–∞—Ä
    'RU-LEN': { left: '27%', top: '26%' },     // –°–∞–Ω–∫—Ç-–ü–µ—Ç–µ—Ä–±—É—Ä–≥
    'RU-NIZ': { left: '35%', top: '46%' },     // –ù–∏–∂–Ω–∏–π –ù–æ–≤–≥–æ—Ä–æ–¥
    'RU-TAM': { left: '32%', top: '53%' },     // –¢–∞–º–±–æ–≤
    'RU-SAM': { left: '43%', top: '56%' },     // –¢–æ–ª—å—è—Ç—Ç–∏
    'RU-TYU': { left: '58%', top: '43%' },     // –¢—é–º–µ–Ω—å
    'RU-ULY': { left: '39%', top: '54%' },     // –£–ª—å—è–Ω–æ–≤—Å–∫
    'RU-BA': { left: '48%', top: '52%' },      // –£—Ñ–∞
    'RU-CHE': { left: '52%', top: '48%' }      // –ß–µ–ª—è–±–∏–Ω—Å–∫
  };

  return coordinates[code] || { left: '50%', top: '50%' }; // –î–µ—Ñ–æ–ª—Ç–Ω–∞—è –ø–æ–∑–∏—Ü–∏—è
};

