  useEffect(() => {
    // –ó–∞–≥—Ä—É–∑–∫–∞ –∏ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–∞—Ä—Ç—ã
    const initializeMap = async () => {
      try {
        console.log('–ó–∞–≥—Ä—É–∂–∞—é GeoJSON –¥–∞–Ω–Ω—ã–µ...');
        const response = await fetch('/russia-geo.json');

        if (!response.ok) {
          throw new Error(`HTTP –æ—à–∏–±–∫–∞! –°—Ç–∞—Ç—É—Å: ${response.status}`);
        }

        const geoData: GeoJSON = await response.json();
        console.log('GeoJSON –¥–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω—ã');

        if (svgRef.current) {
          console.log('–†–µ–Ω–¥–µ—Ä—é –∫–∞—Ä—Ç—É...');
          renderMap(geoData);
        }

        // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
        const handleResize = () => {
          if (svgRef.current) {
            renderMap(geoData);
          }
        };

        window.addEventListener('resize', handleResize);

        // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
        return () => {
          window.removeEventListener('resize', handleResize);
        };
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ GeoJSON –¥–∞–Ω–Ω—ã—Ö:', error);
      }
    };

    initializeMap();
  }, []);

  // –ó–∞–∫—Ä—ã—Ç—å –ø–æ–ø–∞–ø
  const closeNoBranchPopup = () => {
    setShowNoBranchPopup(false);
  };

  // –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞—Ä—Ç—ã
  const renderMap = (geoData: GeoJSON) => {
    if (!svgRef.current) return;

    // –û—á–∏—â–∞–µ–º SVG
    d3.select(svgRef.current).selectAll('*').remove();

    // –ü–æ–ª—É—á–∞–µ–º —Ñ–∞–∫—Ç–∏—á–µ—Å–∫–∏–π —Ä–∞–∑–º–µ—Ä –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
    const actualWidth = mapContainerRef.current?.offsetWidth || 800;
    const aspectRatio = 1.8; // –ë–æ–ª–µ–µ –∫–æ–º–ø–∞–∫—Ç–Ω–æ–µ —Å–æ–æ—Ç–Ω–æ—à–µ–Ω–∏–µ —Å—Ç–æ—Ä–æ–Ω
    const height = actualWidth / aspectRatio;

    // –ú–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —à–∏—Ä–∏–Ω—ã –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
    const baseScale = (actualWidth / 1182) * 600;

    // –ö–æ—Ä—Ä–µ–∫—Ç–∏—Ä—É–µ–º —Ü–µ–Ω—Ç—Ä –¥–ª—è –ª—É—á—à–µ–≥–æ –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –∫–∞—Ä—Ç—ã –†–æ—Å—Å–∏–∏
    const projection = d3.geoAlbers()
      .rotate([-105, 0])
      .center([0, 65]) // –°–º–µ—â–∞–µ–º —Ü–µ–Ω—Ç—Ä –≤—ã—à–µ –¥–ª—è –±–æ–ª–µ–µ –∫–æ–º–ø–∞–∫—Ç–Ω–æ–π –∫–∞—Ä—Ç—ã
      .parallels([50, 70])
      .scale(baseScale * 1.2) // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º –º–∞—Å—à—Ç–∞–± –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è –ø—Ä–æ—Å—Ç—Ä–∞–Ω—Å—Ç–≤–∞
      .translate([actualWidth / 2, height / 2]); // –¶–µ–Ω—Ç—Ä–∏—Ä—É–µ–º –±–µ–∑ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–≥–æ —Å–º–µ—â–µ–Ω–∏—è

    // –°–æ–∑–¥–∞–µ–º path –≥–µ–Ω–µ—Ä–∞—Ç–æ—Ä
    const path = d3.geoPath().projection(projection);

    // –°–æ–∑–¥–∞–µ–º –∞–¥–∞–ø—Ç–∏–≤–Ω—ã–π SVG –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä
    const svg = d3.select(svgRef.current)
      .attr('width', '100%')
      .attr('height', height)
      .attr('viewBox', `0 0 ${actualWidth} ${height}`)
      .style('max-width', '100%')
      .style('height', 'auto')
      .style('overflow', 'visible')
      .attr('preserveAspectRatio', 'xMidYMid meet');

    // –î–æ–±–∞–≤–ª—è–µ–º –≥—Ä—É–ø–ø—É –¥–ª—è –≥—Ä–∞–Ω–∏—Ü —Å—Ç—Ä–∞–Ω—ã
    const boundaryGroup = svg.append('g')
      .attr('class', 'boundary-regions');

    // –°–æ–∑–¥–∞–µ–º –∏ —Å—Ç–∏–ª–∏–∑—É–µ–º –ø—É—Ç–∏ –¥–ª—è —Ä–µ–≥–∏–æ–Ω–æ–≤
    svg.append('g')
      .selectAll('.region')
      .data(geoData.features)
      .enter()
      .append('path')
      .attr('class', (d: any) => {
        const regionName = d.properties.name;
        return regionsWithBranches.includes(regionName) ? 'region has-branch' : 'region';
      })
      .attr('d', (d: any) => path(d) as string)
      .attr('data-region', (d: any) => regionNameToCode[d.properties.name] || '')
      .attr('data-name', (d: any) => d.properties.name)
      .on('mouseover', function(event: any, d: any) {
        // –û—Ç–æ–±—Ä–∞–∂–∞–µ–º –≤—Å–ø–ª—ã–≤–∞—é—â—É—é –ø–æ–¥—Å–∫–∞–∑–∫—É
        if (tooltipRef.current) {
          tooltipRef.current.style.visibility = 'visible';
          tooltipRef.current.style.left = event.pageX + 10 + 'px';
          tooltipRef.current.style.top = event.pageY - 20 + 'px';
          tooltipRef.current.innerHTML = `
            <div>${d.properties.name}</div>
            ${regionsWithBranches.includes(d.properties.name) ? '<div class="has-branch">–ï—Å—Ç—å —Ñ–∏–ª–∏–∞–ª</div>' : ''}
          `;
        }
      })
      .on('mousemove', function(event: any) {
        if (tooltipRef.current) {
          tooltipRef.current.style.left = event.pageX + 10 + 'px';
          tooltipRef.current.style.top = event.pageY - 20 + 'px';
        }
      })
      .on('mouseout', function(event: any, d: any) {
        if (tooltipRef.current) {
          tooltipRef.current.style.visibility = 'hidden';
        }
      })
      .on('click', function(event: any, d: any) {
        const regionName = d.properties.name;

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —Ñ–∏–ª–∏–∞–ª –≤ —Ä–µ–≥–∏–æ–Ω–µ
        if (regionsWithBranches.includes(regionName)) {
          // –ù–∞—Ö–æ–¥–∏–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Ñ–∏–ª–∏–∞–ª–µ –ø–æ –∏–º–µ–Ω–∏ —Ä–µ–≥–∏–æ–Ω–∞
          const branch = branchesData.find(b => b.regionName === regionName);

          if (branch) {
            setSelectedBranch(branch);
            setSelectedRegion(branch.regionCode);

            // –£–¥–∞–ª—è–µ–º –∫–ª–∞—Å—Å selected —Å–æ –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤ –∏ –≤—ã–¥–µ–ª—è–µ–º –≤—ã–±—Ä–∞–Ω–Ω—ã–π
            d3.selectAll('.region').classed('selected', false);
            d3.select(event.currentTarget).classed('selected', true);
          }
        } else {
          // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–æ–ø–∞–ø –æ–± –æ—Ç—Å—É—Ç—Å—Ç–≤–∏–∏ —Ñ–∏–ª–∏–∞–ª–∞
          setNoBranchRegionName(regionName);
          setShowNoBranchPopup(true);
        }
      });

    // –î–æ–±–∞–≤–ª—è–µ–º –≥—Ä–∞–Ω–∏—Ü—É –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤ –†–æ—Å—Å–∏–∏ (–æ–±–≤–æ–¥–∫—É)
    // –°–æ–∑–¥–∞–µ–º –∫–æ–ø–∏—é –≥—Ä–∞–Ω–∏—Ü –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤ –¥–ª—è –Ω–µ–æ–Ω–æ–≤–æ–π –ø–æ–¥—Å–≤–µ—Ç–∫–∏
    boundaryGroup.append('path')
      .datum({
        type: 'FeatureCollection',
        features: geoData.features
      })
      .attr('d', path as any)
      .attr('class', 'russia-boundary');
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è –≤—ã–±–æ—Ä–∞ —Ñ–∏–ª–∏–∞–ª–∞ –≤ –≤—ã–ø–∞–¥–∞—é—â–µ–º —Å–ø–∏—Å–∫–µ
  const handleBranchSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const branchId = parseInt(e.target.value);
    if (!branchId) {
      setSelectedBranch(null);
      setSelectedRegion(null);

      // –°–Ω–∏–º–∞–µ–º –≤—ã–¥–µ–ª–µ–Ω–∏–µ —Å–æ –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤
      d3.selectAll('.region').classed('selected', false);
      return;
    }

    const branch = branchesData.find(branch => branch.id === branchId);
    if (branch) {
      setSelectedBranch(branch);
      setSelectedRegion(branch.regionCode);

      // –£–¥–∞–ª—è–µ–º –≤—ã–¥–µ–ª–µ–Ω–∏–µ —Å–æ –≤—Å–µ—Ö —Ä–µ–≥–∏–æ–Ω–æ–≤
      d3.selectAll('.region').classed('selected', false);

      // –ù–∞—Ö–æ–¥–∏–º —Ä–µ–≥–∏–æ–Ω –ø–æ –∏–º–µ–Ω–∏ –∏ –≤—ã–¥–µ–ª—è–µ–º –µ–≥–æ
      const regions = d3.selectAll('.region');
      regions.each(function(d: any) {
        if (d.properties.name === branch.regionName) {
          d3.select(this).classed('selected', true);
        }
      });
    }
  };

  return (
    <div className="py-6">
      <div className="flex flex-col gap-8">
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ –∏ –≤—ã–ø–∞–¥–∞—é—â–∏–π —Å–ø–∏—Å–æ–∫ */}
        <div className="mx-auto text-center">
          <h2 className="text-2xl font-bold mb-2">–ù–∞—à–∏ —Ñ–∏–ª–∏–∞–ª—ã –Ω–∞ –∫–∞—Ä—Ç–µ –†–æ—Å—Å–∏–∏</h2>
          <p className="text-sm text-muted-foreground mb-6">
            –í—ã–±–µ—Ä–∏—Ç–µ —Ä–µ–≥–∏–æ–Ω –¥–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ —Ñ–∏–ª–∏–∞–ª–µ
          </p>
        </div>

        <div className="flex flex-col lg:flex-row gap-6">
          {/* –õ–µ–≤–∞—è –∫–æ–ª–æ–Ω–∫–∞: —Å–µ–ª–µ–∫—Ç–æ—Ä –∏ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Ñ–∏–ª–∏–∞–ª–µ */}
          <div className="lg:w-1/3 flex flex-col gap-4">
            <div className="relative">
              <select 
                className="w-full p-3 rounded-md bg-background border border-input text-sm appearance-none pr-10"
                value={selectedBranch?.id || ''}
                onChange={handleBranchSelectChange}
              >
                <option value="">–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–∏–ª–∏–∞–ª</option>
                {branchesData.map(branch => (
                  <option key={branch.id} value={branch.id}>
                    {branch.city} ({branch.regionName})
                  </option>
                ))}
              </select>
              <div className="absolute right-3 top-1/2 -translate-y-1/2 text-primary text-opacity-80 text-xs pointer-events-none">‚ñº</div>
            </div>

            {selectedBranch && (
              <div className="branch-info-card">
                <h4 className="text-lg font-medium text-primary">{selectedBranch.city}</h4>
                <div className="text-sm space-y-3 mt-4">
                  <p><span className="font-medium">–†–µ–≥–∏–æ–Ω:</span> {selectedBranch.regionName}</p>
                  <p><span className="font-medium">–ê–¥—Ä–µ—Å:</span> {selectedBranch.address}</p>
                  <p><span className="font-medium">–¢–µ–ª–µ—Ñ–æ–Ω:</span> <a href={`tel:${selectedBranch.phone}`} className="text-primary hover:underline">{selectedBranch.phone}</a></p>
                  <p><span className="font-medium">Email:</span> <a href={`mailto:${selectedBranch.email}`} className="text-primary hover:underline">{selectedBranch.email}</a></p>
                  <p><span className="font-medium">–†–µ–∂–∏–º —Ä–∞–±–æ—Ç—ã:</span> {selectedBranch.workingHours}</p>
                </div>
              </div>
            )}
          </div>

          {/* –ü—Ä–∞–≤–∞—è –∫–æ–ª–æ–Ω–∫–∞: –∫–∞—Ä—Ç–∞ */}
          <div className="lg:w-2/3 relative">
            <div className="map-container w-full" ref={mapContainerRef} style={{ width: '100%', minHeight: '350px', overflow: 'visible' }}>
              <svg ref={svgRef} className="w-full"></svg>
              <div 
                ref={tooltipRef} 
                className="map-tooltip" 
                style={{ visibility: 'hidden' }}
              ></div>

              {/* –ü–æ–ø–∞–ø –æ–± –æ—Ç—Å—É—Ç—Å—Ç–≤–∏–∏ —Ñ–∏–ª–∏–∞–ª–∞ */}
              {showNoBranchPopup && (
                <div className="no-branch-popup">
                  <button className="popup-close" onClick={closeNoBranchPopup}>√ó</button>
                  <p>–í —Ä–µ–≥–∏–æ–Ω–µ <strong>{noBranchRegionName}</strong> –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç —Ñ–∏–ª–∏–∞–ª –∫–æ–º–ø–∞–Ω–∏–∏.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
