import { Badge } from "@/components/ui/badge";

interface StatusBadgeProps {
  status: string;
  className?: string;
}

export function StatusBadge({ status, className }: StatusBadgeProps) {
  const getVariant = () => {
    switch (status.toLowerCase()) {
      case "–∞–∫—Ç–∏–≤–µ–Ω":
      case "–æ–ø–ª–∞—á–µ–Ω":
        return "success";
      case "–æ–∂–∏–¥–∞–µ—Ç –æ–ø–ª–∞—Ç—ã":
      case "–æ–∂–∏–¥–∞–µ—Ç—Å—è":
        return "warning";
      case "–∑–∞–≤–µ—Ä—à–µ–Ω":
        return "neutral";
      case "–æ—Ç–º–µ–Ω–µ–Ω":
        return "destructive";
      default:
        return "neutral";
    }
  };

  return (
    <Badge variant={getVariant()} className={className}>
      {status}
    </Badge>
  );
}

