// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –æ–±—ä—è–≤–ª–µ–Ω–∏–π
const AvitoListings = () => {
  const [listings, setListings] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [lastUpdated, setLastUpdated] = useState(new Date());
  const [error, setError] = useState<string | null>(null);

  const fetchListings = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/avito-listings');
      if (!response.ok) {
        throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö');
      }
      const data = await response.json();
      setListings(data.listings || []);
      setLastUpdated(new Date());
    } catch (err) {
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏—è');
      console.error('Error fetching listings:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchListings();

    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫–∞–∂–¥—ã–µ 30 –º–∏–Ω—É—Ç
    const interval = setInterval(() => {
      fetchListings();
    }, 30 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <Card className="mb-8">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-xl">–ê–∫—Ç—É–∞–ª—å–Ω—ã–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è</CardTitle>
        <div className="flex items-center gap-2">
          <span className="text-sm text-muted-foreground">
            –û–±–Ω–æ–≤–ª–µ–Ω–æ: {lastUpdated.toLocaleTimeString()}
          </span>
          <Button 
            variant="outline" 
            size="sm" 
            onClick={fetchListings} 
            disabled={loading}
            className="gap-2"
          >
            {loading ? (
              <Loader2 size={14} className="animate-spin" />
            ) : (
              <RefreshCw size={14} />
            )}
            –û–±–Ω–æ–≤–∏—Ç—å
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {error && (
          <div className="text-center text-red-500 mb-4">
            {error}
          </div>
        )}

        {loading && listings.length === 0 ? (
          <div className="text-center py-8">
            <Loader2 className="mx-auto h-8 w-8 animate-spin text-muted-foreground" />
            <p className="mt-2 text-muted-foreground">–ó–∞–≥—Ä—É–∂–∞–µ–º –æ–±—ä—è–≤–ª–µ–Ω–∏—è...</p>
          </div>
        ) : (
          <div className="grid gap-6 md:grid-cols-2">
            {listings.map((item, index) => (
              <PropertyItem 
                key={index}
                title={item.title}
                price={item.price}
                description={item.description}
                imageUrl={item.imageUrl}
                avitoUrl={item.avitoUrl}
              />
            ))}
          </div>
        )}

        <p className="text-sm text-muted-foreground mt-4">
          –û–±—ä—è–≤–ª–µ–Ω–∏—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –æ–±–Ω–æ–≤–ª—è—é—Ç—Å—è –∫–∞–∂–¥—ã–µ 30 –º–∏–Ω—É—Ç. 
          –î–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –ø–æ–ª–Ω–æ–π –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –ø–µ—Ä–µ–π–¥–∏—Ç–µ –Ω–∞ —Å–∞–π—Ç –ê–≤–∏—Ç–æ.
        </p>
      </CardContent>
    </Card>
  );
};

export default function PropertyPage() {
  return (
    <MainLayout>
      <section className="py-20 relative">
        <Container>
          <div className="text-center max-w-3xl mx-auto mb-12">
            <h1 className="text-3xl md:text-4xl font-bold mb-6">–†–µ–∞–ª–∏–∑–∞—Ü–∏—è –∏–∑—ä—è—Ç–æ–≥–æ –∏–º—É—â–µ—Å—Ç–≤–∞</h1>
            <p className="text-muted-foreground text-lg">
              –ü—Ä–µ–¥–ª–∞–≥–∞–µ–º –∫ –ø—Ä–∏–æ–±—Ä–µ—Ç–µ–Ω–∏—é –∏–º—É—â–µ—Å—Ç–≤–æ –ø–æ –≤—ã–≥–æ–¥–Ω—ã–º —Ü–µ–Ω–∞–º
